// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {CommonTypes} from "@filecoin/contracts/v0.8/types/CommonTypes.sol";
import "../utils/Structs.sol";

interface IUtils {
    function serializeDealProposal(Structs.DealRequest calldata deal) external view returns (bytes memory);
    function serializeExtraParamsV1(Structs.ExtraParamsV1 memory params) external pure returns (bytes memory);
    function toUint256(CommonTypes.BigInt memory value) external view returns (uint256, bool);
    function getDelegatedAddress(address addr) external pure returns (CommonTypes.FilAddress memory);
    function uintToBigInt(uint256 value) external view returns (CommonTypes.BigInt memory);
    function bigIntToUint(CommonTypes.BigInt memory bigInt) external view returns (uint256);
}

